export const translations = {
  en: {
    dashboard: {
      title: "Task Planning Dashboard",
      emptyTitle: "No tasks yet",
      emptyCTA: "Start your first plan",
      focusTask: "Focus Task",
      stage: "Current Stage",
      progress: "Progress",
      tasksToday: "Today's Tasks",
      actions: {
        enter: "Enter",
        summary: "Summary",
        delete: "Delete"
      },
      status: {
        ongoing: "Ongoing",
        delayed: "Delayed",
        days: "days"
      },
      nav: {
        cards: "Cards",
        focus: "Focus",
        trash: "Trash"
      },
      settings: "Settings",
      language: "Language"
    }
  },
  zh: {
    dashboard: {
      title: "任务计划工作台",
      emptyTitle: "你还没有任务",
      emptyCTA: "规划你的第一个大事",
      focusTask: "焦点任务",
      stage: "当前阶段",
      progress: "进度",
      tasksToday: "今日任务",
      actions: {
        enter: "进入",
        summary: "总结",
        delete: "删除"
      },
      status: {
        ongoing: "进行中",
        delayed: "已拖延",
        days: "天"
      },
      nav: {
        cards: "卡片",
        focus: "专注",
        trash: "回收站"
      },
      settings: "设置",
      language: "语言"
    }
  }
};

export type Language = 'en' | 'zh';

export type TranslationKey = 
  | 'dashboard.title'
  | 'dashboard.emptyTitle'
  | 'dashboard.emptyCTA'
  | 'dashboard.focusTask'
  | 'dashboard.stage'
  | 'dashboard.progress'
  | 'dashboard.tasksToday'
  | 'dashboard.actions.enter'
  | 'dashboard.actions.summary'
  | 'dashboard.actions.delete'
  | 'dashboard.status.ongoing'
  | 'dashboard.status.delayed'
  | 'dashboard.status.days'
  | 'dashboard.nav.cards'
  | 'dashboard.nav.focus'
  | 'dashboard.nav.trash'
  | 'dashboard.settings'
  | 'dashboard.language';

export function t(key: TranslationKey, lang: Language = 'en'): string {
  const keys = key.split('.');
  let value: any = translations[lang];

  for (const k of keys) {
    if (value && value[k]) {
      value = value[k];
    } else {
      return key; // Fallback to the key if translation not found
    }
  }

  return value;
}
