import React, { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useLanguage } from '../lib/language-context';
import { Button } from '../components/ui/button';

const ClarifyPage: React.FC = () => {
  const { t } = useLanguage();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [taskTitle, setTaskTitle] = useState<string>('');

  useEffect(() => {
    // Get the task title from URL params
    const title = searchParams.get('taskTitle');
    if (!title) {
      // If no task title is provided, redirect to dashboard
      navigate('/dashboard');
      return;
    }

    setTaskTitle(decodeURIComponent(title));
  }, [searchParams, navigate]);

  const handleBackToDashboard = () => {
    navigate('/dashboard');
  };

  // This is a placeholder for the clarify page
  // In a real implementation, this would have more functionality
  return (
    <div className="min-h-screen zen-bg p-6">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-8">
        <h1 className="text-2xl font-bold mb-6">正在规划你的目标</h1>

        <div className="mb-8 p-4 bg-indigo-50 rounded-lg border border-indigo-100">
          <h2 className="text-lg font-medium text-indigo-800 mb-2">你的目标是：</h2>
          <p className="text-xl">{taskTitle}</p>
        </div>

        <div className="mb-8">
          <h2 className="text-lg font-medium mb-4">为了更好地帮助你，我们需要了解更多信息...</h2>
          <p className="text-gray-600 mb-2">
            这是澄清页面的占位内容。在实际实现中，这里会显示一系列问题来收集更多与用户目标相关的信息。
          </p>
        </div>

        <div className="flex justify-between">
          <Button 
            variant="outline" 
            onClick={handleBackToDashboard}
          >
            返回首页
          </Button>

          <Button 
            onClick={handleBackToDashboard} // 暂时也返回首页
          >
            继续
          </Button>
        </div>
      </div>
    </div>
  );
};

export default ClarifyPage;
