import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../../lib/language-context';

interface MiniTreeProps {
  growthStage: number; // 0-5
  progress: number; // 0-100
}

const MiniTreeVisual: React.FC<MiniTreeProps> = ({ growthStage, progress }) => {
  const { t } = useLanguage();

  // Ê†ëÊú®emojiÂàóË°®ÔºåÂØπÂ∫î‰∏çÂêåÁîüÈïøÈò∂ÊÆµ
  const treeEmojis = ['üå±', 'üåø', 'üå¥', 'üå≤', 'üå≥', 'üéÑ'];

  // Èò∂ÊÆµÂêçÁß∞
  const stageNames = [
    t('tree.stages.seed'), 
    t('tree.stages.sprout'), 
    t('tree.stages.sapling'), 
    t('tree.stages.growing'), 
    t('tree.stages.lush'), 
    t('tree.stages.fruiting')
  ];

  // Ê†ëÁöÑÈ¢úËâ≤Ê†πÊçÆÁîüÈïøÈò∂ÊÆµÂèòÂåñ
  const getBackgroundColor = () => {
    const colors = [
      'bg-amber-50', // ÁßçÂ≠ê
      'bg-green-50', // ÂπºËãó
      'bg-green-100', // Â∞èÊ†ë
      'bg-green-100', // ÊàêÈïø‰∏≠
      'bg-green-100', // ËåÇÁõõ
      'bg-blue-50', // Á°ïÊûúÁ¥ØÁ¥Ø
    ];
    return colors[growthStage];
  };

  return (
    <div className="relative w-12 h-12 flex items-center justify-center">
      {/* ËÉåÊôØÂúÜÂúàÂèäËøõÂ∫¶ÁéØ */}
      <div className={`absolute inset-0 rounded-full ${getBackgroundColor()} flex items-center justify-center`}>
        <motion.span
          initial={{ scale: 0.5, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          className="text-2xl"
        >
          {treeEmojis[growthStage]}
        </motion.span>
      </div>

      {/* ËøõÂ∫¶ÁéØ */}
      <svg className="absolute inset-0" viewBox="0 0 36 36">
        <circle 
          cx="18" 
          cy="18" 
          r="16" 
          fill="none" 
          stroke="#e5e7eb" 
          strokeWidth="3"
        />
        <motion.circle
          initial={{ strokeDashoffset: 100 }}
          animate={{ strokeDashoffset: 100 - progress }}
          cx="18"
          cy="18"
          r="16"
          fill="none"
          stroke={growthStage < 3 ? "#22c55e" : "#10b981"}
          strokeWidth="3"
          strokeDasharray="100"
          strokeLinecap="round"
          transform="rotate(-90 18 18)"
        />
      </svg>

      {/* ÊÇ¨ÂÅúÊèêÁ§∫ */}
      <div className="absolute left-0 -top-8 opacity-0 group-hover:opacity-100 transition-opacity w-max pointer-events-none">
        <div className="bg-white text-xs px-2 py-1 rounded border shadow-sm text-gray-700">
          {stageNames[growthStage]} ({progress}%)
        </div>
      </div>
    </div>
  );
};

export default MiniTreeVisual;
