export type TaskStatus = 'active' | 'completed' | 'deleted';

export type TaskPriority = 'high' | 'medium' | 'low';

export interface Task {
  id: string;
  title: string;
  description?: string;
  dueDate?: Date;
  status: TaskStatus;
  priority: TaskPriority;
  completed: boolean;
}

export type PlanStatus = 'ongoing' | 'completed' | 'deleted';

export interface PlanStage {
  id: string;
  title: string;
  tasks: Task[];
  completed: boolean;
}

export interface Plan {
  id: string;
  title: string;
  description?: string;
  significance?: string; // 任务意义描述
  currentStageId: string;
  stages: PlanStage[];
  createdAt: Date;
  updatedAt: Date;
  status: PlanStatus;
  progress: number; // 0-100
  delayedDays?: number;
  growthStage?: number; // 0-5树木成长阶段
  aiInteractions?: number; // AI交互次数
}

// 计算任务的树木成长阶段
export const calculateGrowthStage = (plan: Plan): number => {
  if (!plan) return 0;

  // 基础阶段基于进度
  const baseStage = Math.floor(plan.progress / 20);

  // AI互动奖励（每5次交互可以提升一个阶段，但最多提升1级）
  const aiBonus = Math.min(1, Math.floor((plan.aiInteractions || 0) / 5));

  // 最终阶段 (0-5)
  return Math.min(5, baseStage + aiBonus);
};

