import React, { useState, useRef, useEffect } from 'react';
import { Button } from '../../ui/button';
import { motion, AnimatePresence } from 'framer-motion';
import MessageBubble from './message-bubble';
import ThinkingIndicator from './thinking-indicator';
import { toast } from 'sonner';

// 定义消息类型
export interface Message {
  id: string;
  role: 'user' | 'assistant' | 'system';
  content: string;
  timestamp: Date;
  references?: string[]; // 引用的文件或资源
}

// 定义AI模型类型
type AIModel = 'claude-3.7-sonnet' | 'claude-3-opus' | 'gpt-4'; 

// 定义AI模式类型
type AIMode = 'agent' | 'chat';

interface AIChatProps {
  initialMessages?: Message[];
  onSendMessage?: (message: string, references?: string[]) => Promise<string>;
}

const AIChat: React.FC<AIChatProps> = ({ 
  initialMessages = [], 
  onSendMessage 
}) => {
  const [messages, setMessages] = useState<Message[]>(initialMessages);
  const [inputValue, setInputValue] = useState('');
  const [status, setStatus] = useState<'idle' | 'thinking' | 'responding' | 'error'>('idle');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [selectedModel, setSelectedModel] = useState<AIModel>('claude-3.7-sonnet');
  const [mode, setMode] = useState<AIMode>('agent');
  const [isModelDropdownOpen, setIsModelDropdownOpen] = useState(false);
  const [references, setReferences] = useState<string[]>([]);
  const [showReferenceSelector, setShowReferenceSelector] = useState(false);
  const [availableFiles, setAvailableFiles] = useState([
    'workspace.txt', 
    'project.json', 
    'README.md', 
    'src/components/features/plan-tree/plan-tree.tsx'
  ]);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // 处理点击外部关闭下拉菜单
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsModelDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // 自动滚动到最新消息
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // 自动调整输入框高度
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = 'auto';
      inputRef.current.style.height = `${Math.min(inputRef.current.scrollHeight, 200)}px`;
    }
  }, [inputValue]);

  const generateId = () => {
    return Date.now().toString(36) + Math.random().toString(36).substring(2);
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || status !== 'idle') return;

    // 创建用户消息
    const userMessage: Message = {
      id: generateId(),
      role: 'user',
      content: inputValue,
      timestamp: new Date(),
      references: references.length > 0 ? [...references] : undefined
    };

    // 更新状态并清空输入
    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setReferences([]);
    setShowReferenceSelector(false);
    if (inputRef.current) {
      inputRef.current.style.height = 'auto';
    }
    setStatus('thinking');

    try {
      // 如果没有提供onSendMessage回调，使用模拟响应
      const response = onSendMessage 
        ? await onSendMessage(inputValue, references) 
        : await simulateResponse(inputValue, references);

      // 添加AI回复
      const assistantMessage: Message = {
        id: generateId(),
        role: 'assistant',
        content: response,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, assistantMessage]);
      setStatus('idle');
    } catch (error) {
      console.error('Failed to get AI response:', error);
      setStatus('error');

      // 添加错误消息
      const errorMessage: Message = {
        id: generateId(),
        role: 'system',
        content: '抱歉，发生了错误。请稍后再试。',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, errorMessage]);
      setTimeout(() => setStatus('idle'), 3000);
    }
  };

  // 模拟AI响应，仅用于演示
  const simulateResponse = async (message: string, refs?: string[]): Promise<string> => {
    // 返回模拟的响应
    return new Promise((resolve) => {
      const responses = [
        "我理解你的问题。让我帮你分析一下...",
        "这是一个很好的问题。根据我的分析...",
        "我可以协助你解决这个问题。首先，你需要...",
        "让我思考一下最佳的解决方案。",
        "根据最新的研究和最佳实践，我建议..."
      ];

      let response = responses[Math.floor(Math.random() * responses.length)];
      response += ` 你的问题是关于"${message}"的。`;

      // 如果有引用文件，添加相关内容
      if (refs && refs.length > 0) {
        response += ` 我已查看了你引用的文件: ${refs.join(', ')}。`;
      }

      if (mode === 'agent') {
        response += " 作为智能体模式，我会主动帮你执行任务和提供解决方案。";
      } else {
        response += " 作为对话模式，我会回答你的问题，但不会主动执行任务。";
      }

      // 模拟网络延迟
      setTimeout(() => {
        resolve(response);
      }, 1500);
    });
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    } else if (e.key === '@') {
      setShowReferenceSelector(true);
    }
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const handleReferenceSelect = (file: string) => {
    if (!references.includes(file)) {
      setReferences(prev => [...prev, file]);
      toast.success(`已引用 ${file}`);
    }
    setShowReferenceSelector(false);
  };

  const handleRemoveReference = (file: string) => {
    setReferences(prev => prev.filter(ref => ref !== file));
  };

  return (
    <div className={`flex flex-col h-full ${isDarkMode ? 'bg-gray-900 text-gray-200' : 'bg-white text-gray-800'}`}>
      {/* 头部操作区 */}
      <div className={`p-2 flex justify-between items-center border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-200'}`}>
        <div className="flex items-center space-x-2">
          <button 
            className={`rounded-sm p-1 ${isDarkMode ? 'hover:bg-gray-800' : 'hover:bg-gray-100'}`}
            onClick={() => setMessages(initialMessages)}
            aria-label="清除对话"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
          </button>

          {/* 模式切换按钮 */}
          <div className={`rounded-md border ${isDarkMode ? 'border-gray-700' : 'border-gray-200'} p-0.5 flex text-xs`}>
            <button
              onClick={() => setMode('agent')}
              className={`px-2 py-1 rounded-sm ${
                mode === 'agent' 
                  ? (isDarkMode ? 'bg-blue-800 text-white' : 'bg-blue-100 text-blue-700') 
                  : ''
              }`}
            >
              智能体模式
            </button>
            <button
              onClick={() => setMode('chat')}
              className={`px-2 py-1 rounded-sm ${
                mode === 'chat' 
                  ? (isDarkMode ? 'bg-blue-800 text-white' : 'bg-blue-100 text-blue-700') 
                  : ''
              }`}
            >
              对话模式
            </button>
          </div>
        </div>

        <div className="flex items-center space-x-2">
          {/* 模型选择下拉菜单 */}
          <div className="relative" ref={dropdownRef}>
            <button 
              className={`text-xs rounded-md ${isDarkMode ? 'bg-gray-800 hover:bg-gray-700' : 'bg-gray-100 hover:bg-gray-200'} px-2 py-1 flex items-center`}
              onClick={() => setIsModelDropdownOpen(!isModelDropdownOpen)}
            >
              {selectedModel}
              <svg className="w-3 h-3 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {isModelDropdownOpen && (
              <div className={`absolute right-0 mt-1 w-40 rounded-md shadow-lg z-10 ${isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'}`}>
                <div className="py-1">
                  <button
                    className={`block px-4 py-2 text-xs w-full text-left ${selectedModel === 'claude-3.7-sonnet' ? (isDarkMode ? 'bg-gray-700' : 'bg-gray-100') : ''}`}
                    onClick={() => {
                      setSelectedModel('claude-3.7-sonnet');
                      setIsModelDropdownOpen(false);
                    }}
                  >
                    claude-3.7-sonnet
                  </button>
                  <button
                    className={`block px-4 py-2 text-xs w-full text-left ${selectedModel === 'claude-3-opus' ? (isDarkMode ? 'bg-gray-700' : 'bg-gray-100') : ''}`}
                    onClick={() => {
                      setSelectedModel('claude-3-opus');
                      setIsModelDropdownOpen(false);
                    }}
                  >
                    claude-3-opus
                  </button>
                  <button
                    className={`block px-4 py-2 text-xs w-full text-left ${selectedModel === 'gpt-4' ? (isDarkMode ? 'bg-gray-700' : 'bg-gray-100') : ''}`}
                    onClick={() => {
                      setSelectedModel('gpt-4');
                      setIsModelDropdownOpen(false);
                    }}
                  >
                    gpt-4
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* 主题切换按钮 */}
          <button 
            className={`rounded-sm p-1 ${isDarkMode ? 'hover:bg-gray-800' : 'hover:bg-gray-100'}`}
            onClick={toggleTheme}
            aria-label={isDarkMode ? "切换到亮色模式" : "切换到暗色模式"}
          >
            {isDarkMode ? (
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
              </svg>
            ) : (
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
              </svg>
            )}
          </button>
        </div>
      </div>

      {/* 消息历史区域 */}
      <div className={`flex-1 overflow-y-auto p-4 space-y-4 ${isDarkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
        <AnimatePresence>
          {messages.map((message) => (
            <motion.div
              key={message.id}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <MessageBubble message={message} isDarkMode={isDarkMode} />
            </motion.div>
          ))}

          {status === 'thinking' && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <ThinkingIndicator isDarkMode={isDarkMode} />
            </motion.div>
          )}
        </AnimatePresence>

        {/* 用于自动滚动的引用元素 */}
        <div ref={messagesEndRef} />
      </div>

      {/* 底部输入区域 - 类似 Cursor 的风格 */}
      <div className={`p-3 ${isDarkMode ? 'bg-gray-800 border-t border-gray-700' : 'bg-white border-t border-gray-200'}`}>
        {/* 显示已选择的引用文件 */}
        {references.length > 0 && (
          <div className={`flex flex-wrap gap-1 mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
            <span className="text-xs">引用文件:</span>
            {references.map(file => (
              <div 
                key={file} 
                className={`text-xs rounded-md flex items-center px-2 py-1 ${
                  isDarkMode ? 'bg-blue-900 text-blue-200' : 'bg-blue-100 text-blue-800'
                }`}
              >
                <span>{file}</span>
                <button 
                  onClick={() => handleRemoveReference(file)}
                  className="ml-1 text-opacity-70 hover:text-opacity-100"
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
            ))}
          </div>
        )}

        <div className={`rounded-lg overflow-hidden border ${isDarkMode ? 'border-gray-700 bg-gray-800' : 'border-gray-300 bg-white'}`}>
          <textarea
            ref={inputRef}
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder={`向 AI 助手提问... (按 @ 引用文件)`}
            disabled={status !== 'idle'}
            className={`w-full px-3 py-2 resize-none outline-none ${
              isDarkMode ? 'bg-gray-800 text-gray-200 placeholder-gray-500' : 'bg-white text-gray-800 placeholder-gray-400'
            }`}
            rows={1}
            style={{ minHeight: '44px', maxHeight: '200px' }}
          />
          <div className={`flex justify-between items-center px-3 py-2 ${isDarkMode ? 'bg-gray-800' : 'bg-gray-50'}`}>
            <div className="flex items-center space-x-3">
              <div className="text-xs text-gray-500">按 Enter 发送，Shift+Enter 换行</div>

              {/* 添加@引用按钮 */}
              <button
                onClick={() => setShowReferenceSelector(!showReferenceSelector)}
                className={`text-xs ${isDarkMode ? 'text-gray-400 hover:text-gray-300' : 'text-gray-600 hover:text-gray-700'}`}
              >
                <div className="flex items-center">
                  <span>@引用文件</span>
                </div>
              </button>
            </div>

            <Button 
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || status !== 'idle'}
              className={`h-8 px-3 ${
                isDarkMode 
                  ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                  : 'bg-blue-600 hover:bg-blue-700 text-white'
              }`}
            >
              发送
            </Button>
          </div>

          {/* 文件引用选择器 */}
          {showReferenceSelector && (
            <div className={`border-t ${isDarkMode ? 'border-gray-700 bg-gray-800' : 'border-gray-200 bg-white'} max-h-48 overflow-y-auto`}>
              <div className="p-2">
                <div className="text-xs font-medium mb-1">选择要引用的文件：</div>
                <div className="space-y-1">
                  {availableFiles.map(file => (
                    <div 
                      key={file}
                      className={`p-1 text-xs rounded cursor-pointer ${
                        isDarkMode 
                          ? 'hover:bg-gray-700' 
                          : 'hover:bg-gray-100'
                      }`}
                      onClick={() => handleReferenceSelect(file)}
                    >
                      {file}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {status === 'error' && (
          <div className="text-xs text-red-500 mt-1 ml-2">发送失败，请重试</div>
        )}
      </div>
    </div>
  );
};

export default AIChat;
