import React from 'react';
import { Message } from './ai-chat';
import { motion } from 'framer-motion';

// 机器人图标组件
const RobotIcon = ({ className = "w-5 h-5" }) => (
  <svg className={className} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M12 2C10.8954 2 10 2.89543 10 4C10 4.74028 10.4022 5.38663 11 5.73244V7H8C5.79086 7 4 8.79086 4 11V16C4 18.2091 5.79086 20 8 20H9V20.2676C8.4022 20.6134 8 21.2597 8 22C8 23.1046 8.89543 24 10 24C11.1046 24 12 23.1046 12 22C12 21.2597 11.5978 20.6134 11 20.2676V20H13V20.2676C12.4022 20.6134 12 21.2597 12 22C12 23.1046 12.8954 24 14 24C15.1046 24 16 23.1046 16 22C16 21.2597 15.5978 20.6134 15 20.2676V20H16C18.2091 20 20 18.2091 20 16V11C20 8.79086 18.2091 7 16 7H13V5.73244C13.5978 5.38663 14 4.74028 14 4C14 2.89543 13.1046 2 12 2ZM8 10C7.44772 10 7 10.4477 7 11C7 11.5523 7.44772 12 8 12C8.55228 12 9 11.5523 9 11C9 10.4477 8.55228 10 8 10ZM16 10C15.4477 10 15 10.4477 15 11C15 11.5523 15.4477 12 16 12C16.5523 12 17 11.5523 17 11C17 10.4477 16.5523 10 16 10ZM8 14C8 15.6569 9.34315 17 11 17H13C14.6569 17 16 15.6569 16 14H8Z" fill="currentColor"/>
  </svg>
);

interface MessageBubbleProps {
  message: Message;
  isDarkMode?: boolean;
}

const MessageBubble: React.FC<MessageBubbleProps> = ({ message, isDarkMode = false }) => {
  // 获取消息头部样式
  const getHeaderStyle = () => {
    switch (message.role) {
      case 'user':
        return 'border-blue-600';
      case 'assistant':
        return isDarkMode ? 'border-gray-600' : 'border-gray-300';
      case 'system':
        return isDarkMode ? 'border-amber-800' : 'border-amber-300';
      default:
        return isDarkMode ? 'border-gray-600' : 'border-gray-300';
    }
  };

  // 获取内容区域样式
  const getContentStyle = () => {
    switch (message.role) {
      case 'user':
        return isDarkMode 
          ? 'bg-transparent text-gray-200' 
          : 'bg-transparent text-gray-800';
      case 'assistant':
        return isDarkMode 
          ? 'bg-transparent text-gray-200' 
          : 'bg-transparent text-gray-800';
      case 'system':
        return isDarkMode 
          ? 'bg-gray-800 text-amber-100 border border-gray-700 rounded-md px-3 py-2' 
          : 'bg-amber-50 text-amber-800 border border-amber-100 rounded-md px-3 py-2';
      default:
        return isDarkMode 
          ? 'bg-transparent text-gray-200' 
          : 'bg-transparent text-gray-800';
    }
  };

  // 格式化时间戳
  const formatTime = (date: Date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // 消息内容可能包含代码块，我们需要渲染它
  const renderContent = (content: string) => {
    // 简单的代码块识别和渲染
    const codeBlockRegex = /```([a-z]*)\n([\s\S]*?)```/g;
    let lastIndex = 0;
    const parts = [];
    let match;

    // 提取并处理代码块
    while ((match = codeBlockRegex.exec(content)) !== null) {
      // 添加代码块之前的文本
      if (match.index > lastIndex) {
        parts.push(
          <span key={`text-${lastIndex}`} className="whitespace-pre-wrap">
            {content.substring(lastIndex, match.index)}
          </span>
        );
      }

      // 添加代码块
      const language = match[1] || 'plaintext';
      const code = match[2];
      parts.push(
        <div key={`code-${match.index}`} className={`my-2 ${isDarkMode ? 'bg-gray-900' : 'bg-gray-800'} text-gray-200 p-3 rounded-md overflow-x-auto`}>
          <div className="text-xs text-gray-400 mb-1">{language}</div>
          <pre className="font-mono text-sm">{code}</pre>
        </div>
      );

      lastIndex = match.index + match[0].length;
    }

    // 添加最后一部分文本（如果有）
    if (lastIndex < content.length) {
      parts.push(
        <span key={`text-${lastIndex}`} className="whitespace-pre-wrap">
          {content.substring(lastIndex)}
        </span>
      );
    }

    return parts.length > 0 ? parts : <span className="whitespace-pre-wrap">{content}</span>;
  };

  return (
    <div className="mb-6">
      {/* 消息头部 - Cursor风格 */}
      <div className={`flex items-center gap-2 mb-2 ${
        message.role === 'system' ? 'hidden' : ''
      }`}>
        {/* 头像 */}
        {message.role === 'assistant' ? (
          <div className={`w-5 h-5 rounded-full flex-shrink-0 overflow-hidden ${isDarkMode ? 'bg-blue-600' : 'bg-blue-500'} flex items-center justify-center text-white`}>
            <RobotIcon className="w-3.5 h-3.5" />
          </div>
        ) : message.role === 'user' ? (
          <div className={`w-5 h-5 rounded-full flex-shrink-0 overflow-hidden ${isDarkMode ? 'bg-blue-700' : 'bg-blue-100'} flex items-center justify-center`}>
            <span className={`text-xs font-medium ${isDarkMode ? 'text-white' : 'text-blue-600'}`}>U</span>
          </div>
        ) : null}

        {/* 角色名称 */}
        <div className={`text-xs font-medium ${
          message.role === 'assistant' 
            ? (isDarkMode ? 'text-gray-300' : 'text-gray-700')
            : message.role === 'user'
              ? (isDarkMode ? 'text-blue-400' : 'text-blue-600')
              : ''
        }`}>
          {message.role === 'user' ? '你' : message.role === 'system' ? '系统' : ''}
        </div>

        {/* 引用文件标签 */}
        {message.references && message.references.length > 0 && (
          <div className="flex flex-wrap gap-1 ml-1">
            {message.references.map(file => (
              <div 
                key={file} 
                className={`text-xs rounded px-1.5 py-0.5 flex items-center ${
                  message.role === 'user'
                    ? (isDarkMode ? 'bg-blue-900/50 text-blue-300' : 'bg-blue-100 text-blue-700')
                    : (isDarkMode ? 'bg-gray-800 text-gray-300 border border-gray-700' : 'bg-gray-100 text-gray-700')
                }`}
              >
                <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
                <span>{file}</span>
              </div>
            ))}
          </div>
        )}

        {/* 时间戳 */}
        <div className={`text-xs ${
          isDarkMode ? 'text-gray-500' : 'text-gray-400'
        } ml-auto`}>
          {formatTime(message.timestamp)}
        </div>
      </div>

      {/* 消息内容 - Cursor风格 */}
      <div className={`pl-7 ${getContentStyle()}`}>
        <div className="text-sm">
          {renderContent(message.content)}
        </div>
      </div>
    </div>
  );
};

export default MessageBubble;
