export function t(key, lang = 'en') {
  const keys = key.split('.');
  let value = translations[lang];
  for (const k of keys) {
    if (value && value[k]) {
      value = value[k];
    } else {
      return key; // Fallback to the key if translation not found
    }
  }
  return value;
}
