import React from 'react';
import { Message } from './ai-chat';
import { motion } from 'framer-motion';

interface MessageBubbleProps {
  message: Message;
  isDarkMode?: boolean;
}

const MessageBubble: React.FC<MessageBubbleProps> = ({ message, isDarkMode = false }) => {
  // 根据消息角色和主题设置不同的样式
  const getBubbleStyle = () => {
    switch (message.role) {
      case 'user':
        return isDarkMode 
          ? 'bg-blue-800 text-white border border-blue-700' 
          : 'bg-blue-600 text-white border border-blue-500';
      case 'assistant':
        return isDarkMode 
          ? 'bg-gray-800 text-gray-200 border border-gray-700' 
          : 'bg-white text-gray-800 border border-gray-200';
      case 'system':
        return isDarkMode 
          ? 'bg-amber-800 text-amber-100 border border-amber-700' 
          : 'bg-amber-100 text-amber-800 border border-amber-200';
      default:
        return isDarkMode 
          ? 'bg-gray-800 text-gray-200 border border-gray-700' 
          : 'bg-white text-gray-800 border border-gray-200';
    }
  };

  // 格式化时间戳
  const formatTime = (date: Date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // 消息内容可能包含代码块，我们需要渲染它
  const renderContent = (content: string) => {
    // 简单的代码块识别和渲染
    const codeBlockRegex = /```([a-z]*)\n([\s\S]*?)```/g;
    let lastIndex = 0;
    const parts = [];
    let match;

    // 提取并处理代码块
    while ((match = codeBlockRegex.exec(content)) !== null) {
      // 添加代码块之前的文本
      if (match.index > lastIndex) {
        parts.push(
          <span key={`text-${lastIndex}`} className="whitespace-pre-wrap">
            {content.substring(lastIndex, match.index)}
          </span>
        );
      }

      // 添加代码块
      const language = match[1] || 'plaintext';
      const code = match[2];
      parts.push(
        <div key={`code-${match.index}`} className={`my-2 ${isDarkMode ? 'bg-gray-900' : 'bg-gray-800'} text-gray-200 p-3 rounded-md overflow-x-auto`}>
          <div className="text-xs text-gray-400 mb-1">{language}</div>
          <pre className="font-mono text-sm">{code}</pre>
        </div>
      );

      lastIndex = match.index + match[0].length;
    }

    // 添加最后一部分文本（如果有）
    if (lastIndex < content.length) {
      parts.push(
        <span key={`text-${lastIndex}`} className="whitespace-pre-wrap">
          {content.substring(lastIndex)}
        </span>
      );
    }

    return parts.length > 0 ? parts : <span className="whitespace-pre-wrap">{content}</span>;
  };

  return (
    <div className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'} mb-4`}>
      {message.role === 'assistant' && (
        <div className={`w-8 h-8 mr-2 rounded-full flex-shrink-0 overflow-hidden ${isDarkMode ? 'bg-gray-700' : 'bg-blue-100'} flex items-center justify-center`}>
          <svg className={`w-5 h-5 ${isDarkMode ? 'text-blue-400' : 'text-blue-600'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
          </svg>
        </div>
      )}

      <div className={`max-w-[80%] p-3 rounded-lg ${getBubbleStyle()}`}>
        <div className="text-sm">
          {renderContent(message.content)}
        </div>
        <div className={`text-xs mt-1 ${
          message.role === 'user' 
            ? (isDarkMode ? 'text-blue-300' : 'text-blue-200') 
            : (isDarkMode ? 'text-gray-400' : 'text-gray-500')
        } text-right`}>
          {formatTime(message.timestamp)}
        </div>
      </div>

      {message.role === 'user' && (
        <div className={`w-8 h-8 ml-2 rounded-full flex-shrink-0 overflow-hidden ${isDarkMode ? 'bg-blue-700' : 'bg-blue-100'} flex items-center justify-center`}>
          <span className={`font-medium ${isDarkMode ? 'text-white' : 'text-blue-600'}`}>U</span>
        </div>
      )}
    </div>
  );
};

export default MessageBubble;
