import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useLanguage } from '../lib/language-context';
import { Button } from '../components/ui/button';
import { toast } from 'sonner';
import { motion, AnimatePresence } from 'framer-motion';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'https://jjnucnlvlsaj.sealoshzh.site';

const ClarifyPage: React.FC = () => {
  console.log("[ClarifyPage] Component mounted.");
  const { t } = useLanguage();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [taskTitle, setTaskTitle] = useState<string>('');

  // 用于存储问题和回答
  const [questions, setQuestions] = useState<string[]>([]);
  const [answers, setAnswers] = useState<string[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentInput, setCurrentInput] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isEnded, setIsEnded] = useState(false);

  // 用于自动聚焦到输入框
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    // 获取URL中的任务标题
    const title = searchParams.get('taskTitle');
    if (!title) {
      // 如果没有任务标题，重定向到dashboard
      toast.error('Missing task title');
      navigate('/dashboard');
      return;
    }

    const decodedTitle = decodeURIComponent(title);
    console.log('Received task title:', decodedTitle);

    setTaskTitle(decodedTitle);

    // 开发模式：直接使用模拟数据
    setTimeout(() => {
      const mockQuestions = [
        "你是否有具体的考试时间？",
        "你的目标分数是多少？",
        "每天预计投入多长时间？"
      ];
      setQuestions(mockQuestions);
      setIsLoading(false);
    }, 1000);

    // TODO: 下面的代码仅在API可用时使用
    /*
    // 初始化SSE连接
    const initSSE = async () => {
      try {
        // 首先发送POST请求
        const response = await fetch(`${API_BASE_URL}/api/clarify/start`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ taskTitle: decodedTitle }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        // 然后建立SSE连接
        const eventSource = new EventSource(`${API_BASE_URL}/api/clarify/stream?taskTitle=${encodeURIComponent(title)}`);

        eventSource.onmessage = (event) => {
          if (event.data === '[END]') {
            // 收到结束标记
            setIsEnded(true);
            eventSource.close();
            setIsLoading(false);
          } else {
            // 收到新问题
            setQuestions(prev => [...prev, event.data]);
            setIsLoading(false);
          }
        };

        eventSource.onerror = (error) => {
          console.error('SSE Error:', error);
          toast.error('Connection error. Please try again.');
          eventSource.close();
          setIsLoading(false);
        };

        // 清理函数
        return () => {
          eventSource.close();
        };
      } catch (error) {
        console.error('Error initializing SSE:', error);
        toast.error('Failed to connect to the server. Please try again.');
        setIsLoading(false);
      }
    };

    initSSE();
    */
  }, [searchParams, navigate]);

  // 当新问题到达或者回答提交后，自动聚焦到输入框
  useEffect(() => {
    if (inputRef.current && currentIndex < questions.length && !isEnded) {
      inputRef.current.focus();
    }
  }, [questions, currentIndex, isEnded]);

  // 处理回答提交
  const handleSubmitAnswer = () => {
    if (currentInput.trim() === '') return;

    // 保存当前回答
    setAnswers(prev => [...prev, currentInput.trim()]);
    setCurrentInput('');
    setCurrentIndex(prev => prev + 1);

    // 如果所有问题都已回答，且已收到结束标记，跳转到下一页
    if (currentIndex === questions.length - 1 && isEnded) {
      const allAnswers = [...answers, currentInput.trim()];

      // 延迟一点时间再跳转，让用户看到最后一个回答
      setTimeout(() => {
        navigate('/plan', { 
          state: { 
            taskTitle, 
            answers: allAnswers 
          } 
        });
      }, 1000);
    }
  };

  // 处理输入框键盘事件
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmitAnswer();
    }
  };

  // 处理返回Dashboard
  const handleBackToDashboard = () => {
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen zen-bg p-4 md:p-6">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6 md:p-8">
        <h1 className="text-xl md:text-2xl font-bold mb-6">{t('clarify.title')}</h1>

        {/* 目标显示区域 */}
        <div className="mb-8 p-4 bg-indigo-50 rounded-lg border border-indigo-100">
          <h2 className="text-base md:text-lg font-medium text-indigo-800 mb-2">{t('clarify.yourGoal')}</h2>
          <p className="text-lg md:text-xl">{taskTitle}</p>
        </div>

        {/* 加载状态 */}
        {isLoading && (
          <div className="flex flex-col items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mb-4"></div>
            <p className="text-gray-600">{t('clarify.processing')}</p>
          </div>
        )}

        {/* 问答区域 */}
        <div className="space-y-6 mb-8">
          <AnimatePresence>
            {questions.map((question, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
                className="bg-gray-50 p-4 rounded-lg"
              >
                <p className="text-gray-800 mb-3">{index + 1}. {question}</p>

                {index < currentIndex ? (
                  // 已回答的问题
                  <div className="ml-4 p-3 bg-indigo-50 rounded-lg border border-indigo-100">
                    <p className="text-indigo-800">{answers[index]}</p>
                  </div>
                ) : index === currentIndex && !isEnded ? (
                  // 当前问题的输入框
                  <div className="ml-4">
                    <div className="relative">
                      <input
                        ref={inputRef}
                        type="text"
                        value={currentInput}
                        onChange={(e) => setCurrentInput(e.target.value)}
                        onKeyDown={handleKeyDown}
                        placeholder={t('clarify.answerPlaceholder')}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                        disabled={isLoading}
                      />
                      <div className="absolute right-3 top-2.5 text-xs text-gray-400">
                        {t('clarify.enterToSubmit')}
                      </div>
                    </div>
                  </div>
                ) : null}
              </motion.div>
            ))}
          </AnimatePresence>

          {/* 等待下一个问题 */}
          {questions.length > 0 && currentIndex === questions.length && !isEnded && (
            <div className="flex items-center justify-center py-4">
              <div className="animate-pulse text-indigo-600">{t('clarify.waiting')}</div>
            </div>
          )}
        </div>

        {/* 底部操作区域 */}
        <div className="flex justify-between">
          <Button 
            variant="outline" 
            onClick={handleBackToDashboard}
          >
            {t('clarify.back')}
          </Button>

          {isEnded && currentIndex >= questions.length && (
            <Button 
              onClick={() => navigate('/plan', { 
                state: { taskTitle, answers } 
              })}
            >
              {t('clarify.continue')}
            </Button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ClarifyPage;
