import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../lib/language-context';
import { usePlanStore } from '../lib/store';
import { Plan } from '../types/task';

import FocusTaskCard from '../components/features/dashboard/focus-task-card';
import PlanCard from '../components/features/dashboard/plan-card';
import NewPlanCard from '../components/features/dashboard/new-plan-card';
import EmptyState from '../components/features/dashboard/empty-state';
import BottomNav from '../components/features/dashboard/bottom-nav';
import { Button } from '../components/ui/button';
import { motion } from 'framer-motion';

const DashboardPage = () => {
  const { t, language, setLanguage } = useLanguage();
  const navigate = useNavigate();
  const { plans, fetchPlans, deletePlan } = usePlanStore();
  const [activeTab, setActiveTab] = useState('cards');
  const [selectedPlans, setSelectedPlans] = useState<Plan[]>([]);

  // Get active plans (not deleted)
  const activePlans = plans.filter(plan => plan.status !== 'deleted');

  // Get focus plan (highest progress active plan)
  const focusPlan = activePlans.length > 0 
    ? activePlans.sort((a, b) => b.progress - a.progress)[0] 
    : null;

  // Get deleted plans
  const deletedPlans = plans.filter(plan => plan.status === 'deleted');

  useEffect(() => {
    fetchPlans();
  }, [fetchPlans]);

  useEffect(() => {
    if (activeTab === 'cards') {
      setSelectedPlans(activePlans);
    } else if (activeTab === 'focus') {
      setSelectedPlans(focusPlan ? [focusPlan] : []);
    } else if (activeTab === 'trash') {
      setSelectedPlans(deletedPlans);
    }
  }, [activeTab, plans, activePlans, focusPlan, deletedPlans]);

  const handleCreatePlan = () => {
    navigate('/start');
  };

  const handleNewPlanCreated = (id: string) => {
    // 当新计划创建后，确保我们在卡片视图
    setActiveTab('cards');
  };

  const handleEnterPlan = (id: string) => {
    navigate(`/execution?planId=${id}`);
  };

  const handleSummaryPlan = (id: string) => {
    navigate(`/summary?planId=${id}`);
  };

  const handleDeletePlan = (id: string) => {
    deletePlan(id);
    // 如果在回收站视图，则保持在此视图
    if (activeTab !== 'trash') {
      setActiveTab('cards');
    }
  };

  const handleChangeTab = (id: string) => {
    setActiveTab(id);
  };

  const toggleLanguage = () => {
    setLanguage(language === 'en' ? 'zh' : 'en');
  };

  // 检查是否应该显示"添加新卡片"按钮
  const shouldShowNewCard = activeTab === 'cards' && !plans.some(p => p.title === t('dashboard.newPlan') && p.progress === 0);

  return (
    <div className="min-h-screen zen-bg pb-24">
      {/* Top Bar */}
      <header className="flex justify-between items-center p-6">
        <h1 className="text-2xl font-semibold">Linden AI</h1>
        <div className="flex items-center space-x-2">
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={toggleLanguage}
            aria-label={t('dashboard.language')}
          >
            {language === 'en' ? '中文' : 'EN'}
          </Button>
          <Button 
            variant="ghost" 
            size="icon"
            aria-label={t('dashboard.settings')}
          >
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
