      </header>

      {/* Page Title */}
      <div className="text-center mb-8">
        <h2 className="text-3xl font-bold">{t('dashboard.title')}</h2>
      </div>

      {/* Content */}
      {plans.length === 0 ? (
        <EmptyState onCreatePlan={handleCreatePlan} />
      ) : (
        <div className="px-4 max-w-6xl mx-auto">
          {/* Focus Task Card */}
          {focusPlan && activeTab === 'cards' && (
            <div className="mb-10">
              <div className="text-lg font-medium mb-3 text-center">
                {t('dashboard.focusTask')}
              </div>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <FocusTaskCard 
                  plan={focusPlan} 
                  onEnter={handleEnterPlan} 
                />
              </motion.div>
            </div>
          )}

          {/* Task Cards Grid */}
          <motion.div 
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, staggerChildren: 0.1 }}
          >
            {selectedPlans.map((plan) => (
              <motion.div 
                key={plan.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
              >
                <PlanCard
                  plan={plan}
                  onEnter={handleEnterPlan}
                  onSummary={handleSummaryPlan}
                  onDelete={handleDeletePlan}
                />
              </motion.div>
            ))}

            {/* Add New Plan Card */}
            {shouldShowNewCard && activeTab === 'cards' && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3, delay: 0.1 }}
              >
                <NewPlanCard onPlanCreated={handleNewPlanCreated} />
              </motion.div>
            )}
          </motion.div>
        </div>
      )}

      {/* Bottom Navigation */}
      <BottomNav 
        activeTab={activeTab}
        onChangeTab={handleChangeTab}
      />
    </div>
  );
};

export default DashboardPage;
